RegisterNetEvent('lxr:supreme:openBoard',function(bi) local b=Config.Boards[bi]; if not b then return end; local opts={}; for i,m in ipairs(b.deliveries or {}) do local tag=m.stealth and '[Stealth] ' or ''; opts[#opts+1]={title=tag..(m.label or m.id or ('Mission '..i)),description=m.desc or 'Haul goods.',onSelect=function() TriggerServerEvent('lxr:supreme:start',bi,i) end} end; if lib and lib.registerContext then lib.registerContext({id='lxr_board_'..bi,title=b.name or 'Freight Board',options=opts}); lib.showContext('lxr_board_'..bi) else TriggerServerEvent('lxr:supreme:start',bi,1) end end)
