CreateThread(function() while true do if Config.Wildlife.enabled and math.random(1,100)<= (Config.Wildlife.chancePerMinute or 0) then local p=GetEntityCoords(PlayerPedId()); local model=joaat(Config.Wildlife.models[1] or 'A_C_Wolf_01'); RequestModel(model) while not HasModelLoaded(model) do Wait(0) end; local off=vector3(p.x+math.random(-15,15),p.y+math.random(-15,15),p.z); local ped=CreatePed(model,off.x,off.y,off.z,0.0,true,false,0,0); TaskCombatPed(ped,PlayerPedId(),0,16); if lib and lib.notify then lib.notify({title='Wildlife',description=locale('wildlife'),type='warning'}) end end; Wait(60000) end end)
