Logger={};local function send(w,t,d) if not w then return end PerformHttpRequest(w,function() end,'POST',json.encode({username='LXRSupreme',embeds={{title=t,description=d,color=16762880,timestamp=os.date('!%Y-%m-%dT%H:%M:%SZ')}}}),{['Content-Type']='application/json'}) end;function Logger.delivery(src,msg) send(Config.Discord.deliveries_webhook,'Delivery',GetPlayerName(src)..': '..msg) end;function Logger.levelup(src,msg) send(Config.Discord.levelup_webhook,'Level Up',GetPlayerName(src)..': '..msg) end;function Logger.rep(src,msg) send(Config.Discord.reputation_webhook,'Reputation',GetPlayerName(src)..': '..msg) end;function Logger.black(src,msg) send(Config.Discord.blackmarket_webhook,'Black Market',GetPlayerName(src)..': '..msg) end
